import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

const SearchPage = () => {
    const location = useLocation();
    const searchTerms = new URLSearchParams(location.search).get('q');
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchTerms}`);
                const data = await response.json();
                setSearchResults(data.items || []);
            } catch (error) {
                console.error('Error fetching search results:', error);
            }
        };

        if (searchTerms) {
            fetchData();
        }
    }, [searchTerms]);

    return (
        <div>
            <h1>Search</h1>
            <p>This is a demo SEARCH</p>
            <p>Search Results:</p>
            <ul>
                {searchResults.map((result) => (
                    <li key={result.id}>{result.volumeInfo.title}</li>
                ))}
            </ul>
        </div>
    );
};

export default SearchPage;
