import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import "../styles/searchpage.css"

const SearchPage = () => {
    const { isAuthenticated, shelfContent, setShelfContent } = useAuth();
    const location = useLocation();
    const searchTerms = new URLSearchParams(location.search).get('q');
    const [searchResults, setSearchResults] = useState([]);

    const addToShelf = (book) => {
        // Проверяем, авторизован ли пользователь
        if (isAuthenticated) {
            // Проверяем, есть ли книга уже в полке
            const isBookInShelf = shelfContent.some((shelfBook) => shelfBook.id === book.id);

            if (!isBookInShelf) {
                // Если книги нет в полке, добавляем её
                setShelfContent((prevShelfContent) => [...prevShelfContent, book]);
            }
        } else {
            // Выводим предупреждение, если пользователь не авторизован
            alert('Please login to add books to your shelf.');
        }
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchTerms}`);
                const data = await response.json();
                setSearchResults(data.items || []);
            } catch (error) {
                console.error('Error fetching search results:', error);
            }
        };

        if (searchTerms) {
            fetchData();
        }
    }, [searchTerms]);

    return (
        <div className={"main-search"}>
            <p className={"p"}>Search Results:</p>
            <ul>
                {searchResults.map((result) => (
                    <li key={result.id}>
                        <div>
                            {result.volumeInfo.imageLinks && (
                                <img
                                    src={result.volumeInfo.imageLinks.thumbnail}
                                    alt={`Cover for ${result.volumeInfo.title}`}
                                    className="book-cover"
                                />
                            )}
                        </div>
                        <div>
                            <h3>{result.volumeInfo.title}</h3>
                            {/* Добавляем кнопку "Add to My Shelf" */}
                            <button onClick={() => addToShelf(result)}>Add to My Shelf</button>
                            {/* ... (остальная информация о книге) */}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SearchPage;
