import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import SearchPage from './SearchPage'; // Путь к вашему компоненту SearchPage

const Books = () => {
    const [bookCategories, setBookCategories] = useState([]);

    useEffect(() => {
        // Вместо этого у вас может быть запрос к API для получения категорий книг
        const categoriesData = [
            { id: 1, title: 'Fantasy' },
            { id: 2, title: 'Mystery' },
            // Добавьте другие категории
        ];

        setBookCategories(categoriesData);
    }, []);

    return (
        <div>
            <h1>Books</h1>
            <p>This is a demo BOOKS</p>

            {bookCategories.map((category) => (
                <div key={category.id}>
                    <h2>{category.title}</h2>

                    <Slider>
                        {/* Здесь используем компонент SearchPage в каждом слайде для отображения результатов поиска */}
                        <SearchPage category={category.title} />
                    </Slider>
                </div>
            ))}
        </div>
    );
};

export default Books;
