import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import RegisterForm from '../components/RegisterForm';

const Login = () => {
    const { login, register, isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const [isRegistering, setIsRegistering] = useState(false);

    const handleLogin = () => {
        if (isRegistering) {
            // Если мы в режиме регистрации, выполняем регистрацию
            register();
        } else {
            // В противном случае - входим
            login();
        }

        // Перенаправляем пользователя на страницу MyShelf в любом случае
        navigate('/shelf');
    };

    const toggleRegistration = () => {
        // Переключаемся между режимами регистрации и авторизации
        setIsRegistering((prev) => !prev);
    };

    return (
        <div className="main-log">
            <h1>{isRegistering ? 'Register' : 'Login'}</h1>
            {isAuthenticated ? (
                <button onClick={handleLogin}>Logout</button>
            ) : (
                <>
                    <button onClick={handleLogin}>{isRegistering ? 'Register' : 'Login'}</button>
                    <button onClick={toggleRegistration}>{isRegistering ? 'Switch to Login' : 'Switch to Register'}</button>
                    {isRegistering && <RegisterForm />}
                </>
            )}
        </div>
    );
};

export default Login;
