import React from 'react';
import { useAuth } from '../context/AuthContext';

const AddForm = ({ bookInfo }) => {
    const { shelfContent, addToShelf } = useAuth();

    const handleAddBook = () => {
        // Проверка наличия необходимых свойств
        if (bookInfo && bookInfo.volumeInfo) {
            // Создаем объект новой книги
            const newBook = {
                id: bookInfo.id, // Используем id из информации о книге
                title: bookInfo.volumeInfo.title,
                authors: bookInfo.volumeInfo.authors || [], // Проверяем наличие авторов
                // Другие свойства книги...
            };

            // Проверка наличия книги в полке
            const isBookInShelf = shelfContent.some((book) => book.id === newBook.id);

            if (!isBookInShelf) {
                // Если книги нет в полке, добавляем ее
                addToShelf(newBook);
            } else {
                // Если книга уже есть в полке, можно показать сообщение или выполнить другие действия
                alert("This book is already in your shelf!");
            }
        }
    };

    return (
        <div>
            <form onSubmit={(e) => e.preventDefault()}>
                <button onClick={handleAddBook}>Add Book</button>
            </form>
        </div>
    );
};

export default AddForm;
