import Slider from 'react-slick';

const BooksSlider = ({ books }) => {
    // Параметры для настройки слайдера
    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
    };

    return (
        <div>
            {/* Заголовок слайдера */}
            <h2>{category} Books</h2>

            {/* Слайдер с книгами */}
            <Slider {...sliderSettings}>
                {books.map((book) => (
                    // JSX для отображения каждой книги в слайдере
                    <div key={book.id}>
                        <img
                            src={book.volumeInfo.imageLinks.thumbnail}
                            alt={book.volumeInfo.title}
                        />
                        <h3>{book.volumeInfo.title}</h3>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default BooksSlider;
