import React, {useEffect, useState} from 'react';
import {fetchBooks} from '../api-service/apiService'; // Замените путь на фактический путь к вашему сервису

const CategoriesSlider = () => {
    const categories = ['fiction', 'history', 'science', 'technology', 'romance'];
    const [booksByCategory, setBooksByCategory] = useState({});

    useEffect(() => {
        const fetchDataForCategories = async () => {
            const booksDataByCategory = {};

            for (const category of categories) {
                const searchTerms = `${category}&maxResults=10`;
                booksDataByCategory[category] = await fetchBooks(searchTerms);
            }

            setBooksByCategory(booksDataByCategory);
        };

        fetchDataForCategories();
    }, []);

    return (
        <div>
            {categories.map((category) => (
                <div key={category}>
                    <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
                    <ul>
                        {booksByCategory[category]?.map((book) => (
                            <div key={book.id} className={"slide"}>
                                <div>
                                    {book.volumeInfo.imageLinks && (
                                        <img
                                            src={book.volumeInfo.imageLinks.thumbnail}
                                            alt={`Cover for ${book.volumeInfo.title}`}
                                            className="book-cover"
                                        />
                                    )}
                                </div>
                                <div>
                                    <h3>{book.volumeInfo.title}</h3>
                                </div>
                            </div>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default CategoriesSlider;
