import React from 'react';
import { useAuth } from '../context/AuthContext';

const AddForm = ({ bookInfo }) => {
    const { shelfContent, setShelfContent } = useAuth();

    const handleAddBook = () => {
        // Добавьте логику для добавления книги в shelfContent, используя информацию о книге
        const newBook = {
            id: shelfContent.length + 1,
            title: bookInfo.volumeInfo.title,
            // Другие свойства книги...
        };

        setShelfContent((prevShelfContent) => [...prevShelfContent, newBook]);
    };

    return (
        <div>
            <form onSubmit={(e) => e.preventDefault()}>
                <button onClick={handleAddBook}>Add Book</button>
            </form>
        </div>
    );
};

export default AddForm;
