import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(() => {
        // При монтировании компонента, читаем значение из локального хранилища
        const storedAuth = localStorage.getItem('auth');
        return storedAuth ? JSON.parse(storedAuth) : false;
    });
    const [shelfContent, setShelfContent] = useState(() => {
        // То же самое для содержимого полки
        const storedShelf = localStorage.getItem('shelfContent');
        return storedShelf ? JSON.parse(storedShelf) : [];
    });

    // Внутри AuthProvider
    const register = (userData) => {
        // Здесь вы можете добавить логику регистрации (например, отправка данных на сервер)
        // В данном случае, просто выведем данные в консоль
        console.log('Registration data:', userData);

        // После успешной регистрации, предполагаем, что пользователь сразу авторизован
        setIsAuthenticated(true);
        localStorage.setItem('auth', JSON.stringify(true));
    };



    const login = () => {
        // Пример успешной аутентификации
        setIsAuthenticated(true);
        // Сохраняем статус аутентификации в локальное хранилище
        localStorage.setItem('auth', JSON.stringify(true));
    };

    const logout = () => {
        // Пример выхода из аккаунта
        setIsAuthenticated(false);
        setShelfContent([]);
        // Сохраняем обновленные данные в локальное хранилище
        localStorage.setItem('auth', JSON.stringify(false));
        localStorage.setItem('shelfContent', JSON.stringify([]));
    };


    return (
        <AuthContext.Provider value={{ isAuthenticated, login, logout, shelfContent, setShelfContent }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
