import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [shelfContent, setShelfContent] = useState([]);

    const login = () => {
        // Здесь можете добавить логику успешной аутентификации
        setIsAuthenticated(true);
    };

    const logout = () => {
        // Здесь можете добавить логику выхода из аккаунта
        setIsAuthenticated(false);
        setShelfContent([]);
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, login, logout, shelfContent, setShelfContent }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
