import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import "../styles/books.css"

const Books = () => {
    const [randomBooks, setRandomBooks] = useState([]);

    useEffect(() => {
        const fetchRandomBooks = async () => {
            try {
                const response = await fetch('https://www.googleapis.com/books/v1/volumes?q=random');
                const data = await response.json();

                if (data.items) {
                    const randomBooksData = data.items.map((item) => ({
                        id: item.id,
                        title: item.volumeInfo.title,
                        authors: item.volumeInfo.authors || [],
                        thumbnail: item.volumeInfo.imageLinks?.thumbnail || '',
                        previewLink: item.volumeInfo.previewLink || '',
                    }));

                    setRandomBooks(randomBooksData);
                }
            } catch (error) {
                console.error('Error fetching random books:', error);
            }
        };

        // Вызываем функцию загрузки рандомных книг при монтировании компонента
        fetchRandomBooks();
    }, []);

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 5000, // Время смены слайда в миллисекундах (здесь 5 секунд)
        afterChange: (currentSlide) => {
            // Обновляем данные после каждой итерации (после каждого слайда)
            fetchRandomBooks();
        },
    };

    return (
        <div>
            <h1>Books</h1>
            <p>This is a demo BOOKS</p>

            <Slider {...settings}>
                {randomBooks.map((book) => (
                    <div key={book.id}>
                        <img src={book.thumbnail} alt={`Cover for ${book.title}`}
                        className={"imgs"}/>
                        <h3>{book.title}</h3>
                        <p>Authors: {book.authors.join(', ')}</p>
                        <p>
                            <a href={book.previewLink} target="_blank" rel="noopener noreferrer">
                                Preview Link
                            </a>
                        </p>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default Books;
