import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(() => {
        const storedAuth = localStorage.getItem('auth');
        return storedAuth ? JSON.parse(storedAuth) : false;
    });
    const [shelfContent, setShelfContent] = useState(() => {
        const storedShelf = localStorage.getItem('shelfContent');
        return storedShelf ? JSON.parse(storedShelf) : [];
    });

    const login = () => {
        setIsAuthenticated(true);
        localStorage.setItem('auth', JSON.stringify(true));
    };

    const logout = () => {
        setIsAuthenticated(false);
        setShelfContent([]);
        localStorage.setItem('auth', JSON.stringify(false));
        localStorage.setItem('shelfContent', JSON.stringify([]));
    };

    const register = (userData) => {
        // В реальном проекте отправляйте данные на сервер для регистрации
        console.log('Registration data:', userData);

        // Пример успешной регистрации (пользователь сразу авторизуется)
        setIsAuthenticated(true);
        localStorage.setItem('auth', JSON.stringify(true));
    };
    

    return (
        <AuthContext.Provider value={{ isAuthenticated, login, logout, register, shelfContent, setShelfContent }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
