import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import RegisterForm from '../components/RegisterForm';

const Login = () => {
    const { login, logout, register, isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const [isRegistering, setIsRegistering] = useState(false);

    const handleLogin = () => {
        if (isAuthenticated) {
            // Если мы уже аутентифицированы, выполняем выход
            logout();
        } else {
            // Если мы в режиме регистрации, выполняем регистрацию
            if (isRegistering) {
                register();
            } else {
                // В противном случае - входим
                login();
            }
        }

        // Перенаправляем пользователя на страницу MyShelf в любом случае
        navigate('/shelf');
    };

    const toggleRegistration = () => {
        // Переключаемся между режимами регистрации и авторизации
        setIsRegistering((prev) => !prev);
    };

    return (
        <div className="main-log">
            <h1>{isAuthenticated ? 'Log out' : isRegistering ? 'Register' : 'Log in'}</h1>
            <button onClick={handleLogin}>{isAuthenticated ? 'Log out' : 'Log in/Register'}</button>
            {isAuthenticated ? null : (
                <>
                    <button onClick={toggleRegistration}>
                        {isRegistering ? 'Switch to Login' : 'Switch to Register'}
                    </button>
                    {isRegistering && <RegisterForm />}
                </>
            )}
        </div>
    );
};

export default Login;
