// components/AddForm.js
import React from 'react';
import { useAuth } from '../context/AuthContext';

const AddForm = ({ bookInfo }) => {
    const { isAuthenticated, addToShelf } = useAuth();

    const handleAddBook = () => {
        if (!isAuthenticated) {
            // Если пользователь не авторизован, выполните необходимые действия (показ сообщения, перенаправление на страницу входа и т. д.)
            alert("Please log in to add books to your shelf.");
            return;
        }

        // Проверка наличия необходимых свойств
        if (bookInfo && bookInfo.volumeInfo) {
            // Передаем полную информацию о книге при добавлении
            addToShelf(bookInfo);
        }
    };

    return (
        <div className={"div-btn"}>
            <form onSubmit={(e) => e.preventDefault()}>
                <button onClick={handleAddBook}>Add Book</button>
            </form>
        </div>
    );
};

export default AddForm;
