import React, { useEffect, useState } from 'react';
import { fetchBooks } from '../api-service/apiService';
import '../styles/slider.css';
import BookCard from './BookCard';

const CategoriesSlider = ({ id }) => {
    const categories = ['fiction', 'history', 'science', 'technology', 'romance'];
    const [booksByCategory, setBooksByCategory] = useState({});
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const fetchDataForCategories = async () => {
            const booksDataByCategory = {};

            for (const category of categories) {
                const searchTerms = `${category}&maxResults=10`;
                booksDataByCategory[category] = await fetchBooks(searchTerms);
            }

            setBooksByCategory(booksDataByCategory);
        };

        fetchDataForCategories();
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % categories.length);
        }, 10000); // Интервал переключения, например, каждые 10 секунд

        return () => clearInterval(interval);
    }, [categories.length]);

    return (
        <div className={`carousel-container slider-${id}`}>
            <div className="category-carousel">
                <h2 className={"category"}>
                    {categories[currentIndex].charAt(0).toUpperCase() + categories[currentIndex].slice(1)}
                </h2>
            </div>
            <div className="carousel">
                <div className="book-list">
                    {booksByCategory[categories[currentIndex]]?.map((book) => (
                        <BookCard key={book.id} book={book} />
                    ))}
                    {booksByCategory[categories[currentIndex]]?.map((book) => (
                        <BookCard key={book.id} book={book} />
                    ))}
                    {/* Повторите книги для включения в начало карусели */}
                </div>
            </div>
        </div>
    );
};

export default CategoriesSlider;
