import { useEffect, useState } from 'react';

const BooksFetcher = ({ category, children }) => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(
                    `https://www.googleapis.com/books/v1/volumes?q=${category}&maxResults=10&key=YOUR_API_KEY`
                );
                const data = await response.json();
                setBooks(data.items);
            } catch (error) {
                console.error('Error fetching books:', error);
            }
        };

        fetchData();
    }, [category]);

    // Передаем полученные данные в компонент-потомок
    return children(books);
};

export default BooksFetcher;
