// components/MyShelf.js
import React from 'react';
import { useAuth } from '../context/AuthContext';

const MyShelf = () => {
    const { isAuthenticated, shelfContent } = useAuth();

    if (!isAuthenticated) {
        return <p className={"info"}>Please login to view your shelf.</p>;
    }

    return (
        <div className="main-shelf">
            <h1>My Shelf</h1>
            <p>Here is your bookshelf content...</p>
            {/* Вывод информации о полке пользователя */}
            {shelfContent.map((book) => (
                <div key={book.id || book.volumeInfo.id}>
                    {book.volumeInfo.imageLinks && (
                        <img
                            src={book.volumeInfo.imageLinks.thumbnail}
                            alt={`Cover for ${book.volumeInfo.title}`}
                            className="book-cover"
                        />
                    )}
                    <p>{book.volumeInfo.title}</p>
                    <p>Authors: {book.volumeInfo.authors?.join(', ')}</p>
                    {/* Вывод других свойств книги, например, даты публикации, описания, категорий, рейтинга, языка и т. д. */}
                </div>
            ))}
        </div>
    );
};

export default MyShelf;
