import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import "../styles/searchpage.css"
import {useAuth} from "../context/AuthContext";

const SearchPage = () => {
    const location = useLocation();
    const searchTerms = new URLSearchParams(location.search).get('q');
    const [searchResults, setSearchResults] = useState([]);

    const { isAuthenticated, shelfContent, setShelfContent } = useAuth();



    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchTerms}`);
                const data = await response.json();
                setSearchResults(data.items || []);
            } catch (error) {
                console.error('Error fetching search results:', error);
            }
        };

        if (searchTerms) {
            fetchData();
        }
    }, [searchTerms]);

    const handleAddToShelf = (book) => {
        // Проверяем, что пользователь авторизован
        if (isAuthenticated) {
            // Проверяем, что книга еще не добавлена в полку
            if (!shelfContent.some((shelfBook) => shelfBook.id === book.id)) {
                // Добавляем книгу в полку
                setShelfContent((prevShelfContent) => [...prevShelfContent, book]);
            }
        } else {
            // Предлагаем пользователю авторизоваться, если он не авторизован
            alert('Please login to add books to your shelf.');
        }
    };

    return (
        <div className={"main-search"}>
            <p className={"p"}>Search Results:</p>
            <ul>
                {searchResults.map((result) => (
                    <li key={result.id}>
                        <div>
                            {result.volumeInfo.imageLinks && (
                                <img
                                    src={result.volumeInfo.imageLinks.thumbnail}
                                    alt={`Cover for ${result.volumeInfo.title}`}
                                    className="book-cover" // Добавьте класс для стилизации обложки
                                />
                            )}
                        </div>
                        <div>
                            <h3>{result.volumeInfo.title}</h3>
                            <p>Authors: {result.volumeInfo.authors?.join(', ')}</p>
                            <p>Published Date: {result.volumeInfo.publishedDate}</p>
                            <p className={"desc"}>Description: {result.volumeInfo.description}</p>
                            <p>Categories: {result.volumeInfo.categories?.join(', ')}</p>
                            <p>Rating: {result.volumeInfo.averageRating} ({result.volumeInfo.ratingsCount} ratings)</p>
                            <p>Language: {result.volumeInfo.language}</p>
                            <p>
                                <a href={result.volumeInfo.previewLink} target="_blank" rel="noopener noreferrer">Preview
                                    Link</a>
                            </p>
                            <button onClick={() => handleAddToShelf(result)}>Add to Shelf</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SearchPage;
